            else if (p == 3) p = 2;
        } else {
            if (p == 1) p = 3;
            else if (p == 3) p = 1;
        }
    }

    cout &lt;&lt; p;
}
</code></pre>

เฉลย CC++ <pre><code>#include &lt;stdio.h&gt;

int main() {
    bool rem[42]={};
    int cnt=0;

    for (int i = 0; i &lt; 10; i++) {
        int a;
        scanf("%d", &amp;a);
        if(!rem[a%42]++) cnt++;
    }

    printf("%d", cnt);
}
</code></pre> <pre><code>#include &lt;iostream&gt;
#include &lt;set&gt;

