    set &lt;int&gt; rem;

    for (int i = 0; i &lt; 10; i++) {
        int a;
        cin &gt;&gt; a;
        rem.insert(a%42);
    }

    cout &lt;&lt; rem.size();
}
</code></pre>

โค้ดเฉลย CC++ <pre><code>#include &lt;stdio.h&gt;
#include &lt;string.h&gt;
int main() {
    char s[100];
    scanf("%s", s);
    int n = strlen(s);
    for (int i = 0; i &lt; n; i++) {
        if(i % 3 == 2) printf("..*.");
        else printf("..#.");
    }
    printf(".\n");

    // บรรทัดที่ 2
    for (int i = 0; i &lt; n; i++) {
        if(i % 3 == 2) printf(".*.*");
        else printf(".#.#");
    }
    printf(".\n");

    // บรรทัดที่ 3
    for (int i = 0; i &lt; n; i++) {
        if (i != 0 &amp;&amp; i % 3 != 1)
            printf("*.%c.", s[i]);
        else
            printf("#.%c.", s[i]);
    }
    if(n % 3 == 0) printf("*\n");
    else printf("#\n");

    // บรรทัดที่ 4
    for (int i = 0; i &lt; n; i++) {
        if(i % 3 == 2) printf(".*.*");
        else printf(".#.#");
    }
    printf(".\n");

    // บรรทัดที่ 5
    for (int i = 0; i &lt; n; i++) {
        if(i % 3 == 2) printf("..*.");
        else printf("..#.");
    }
    printf(".\n");
}
</code></pre> <pre><code>#include &lt;iostream&gt;

using namespace std;

