    }
    if(n % 3 == 0) cout &lt;&lt; "*\n";
    else cout &lt;&lt; "#\n";

    // บรรทัดที่ 4
    for (int i = 0; i &lt; n; i++) {
        if(i % 3 == 2) cout &lt;&lt; ".*.*";
        else cout &lt;&lt; ".#.#";
    }
    cout &lt;&lt; ".\n";

    // บรรทัดที่ 5
    for (int i = 0; i &lt; n; i++) {
        if(i % 3 == 2) cout &lt;&lt; "..*.";
        else cout &lt;&lt; "..#.";
    }
    cout &lt;&lt; ".\n";
}
</code></pre>

